function [matrix]=read_matrix(m, n, name);if nargin==2 | nargin==0,		% No name specified	[filename, pathname] = uigetfile('*.*', 'Choose the file with the matrix!');	name = sprintf('%s%s', pathname, filename);end;if nargin==0,			% No arguments at all => Matrix assumed to be stored by rows	matrix = dlmread(name, ' ');else					% Read matrix with given dimensions	fid = fopen(name, 'r');	matrix = fscanf(fid, '%g', [n m]);	matrix = matrix';	fclose(fid);end;return;