function [sensors, name, pathname, filename] = read_pmg(filename);if nargin==0,   disp(' [sensors, name, pathname, filename] = read_pmg(filename); ');   return;end;if isempty(filename),		% No name specified	[name, pathname] = uigetfile('*.pmg', 'Choose a PMG-position file!');	filename = sprintf('%s%s', pathname, name);end;fid = fopen(filename, 'r');line = fgetl(fid);i=1;while line~=-1,   [text, R] = strtok(line, ':');   [points, values] = strtok(R, ' ');   sensors(1:6,i) = sscanf(values, '%f', 6);   line = fgetl(fid);   i = i+1;end;   