% SCADS2BESA converts Junghoefer SCADS at format to BESA asciifunction [] = scads2besa(FileMat, Trigpoint)if nargin < 2 Trigpoint = input('insert first latency value       ')endif nargin < 1 [AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec]=ReadAvgFile;	 write_avr(AvgMat, [FilePath '.avr'], Trigpoint, 1000/SampRate);else	for FileIndex = 1: size(FileMat, 1)	[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec]=ReadAvgFile(FileMat(FileIndex, :));	write_avr(AvgMat, [FileMat(FileIndex, :) '.avr'], Trigpoint, 1000/SampRate);	end end