function sphere_map(xyz, f);xmax = max(xyz(1,:));xmin = min(xyz(1,:));ymax = max(xyz(2,:));ymin = min(xyz(2,:));zmax= max(xyz(3,:));zmin = min(xyz(3,:));fmax = max(f);fmin = min(f);xlin = linspace(xmin, xmax, length(xyz(1,:)));ylin = linspace(ymin, ymax, length(xyz(2,:)));zlin = linspace(zmin, zmax, length(xyz(3,:)));[X,Y] = meshgrid(xlin, ylin);[XI, YI, ZI] = griddata(xyz(1,:), xyz(2,:), xyz(3,:), X, Y);%FI = interp3(xyz(1,:), xyz(2,:), xyz(3,:), f, X, Y, Z);surf(XI,YI,ZI);