% showTFdirfunction [] = showTFdir(dirname, taxis, faxis);filemat = getfilesindir(dirname)cd(dirname)for index = 1: size(filemat, 1); 	load(filemat(index,:));	pause    AvgWaPower = bslcorrWAMat_div(AvgWaPower, 5:140);	contourf(taxis(5:length(taxis)-5), faxis(5:length(faxis)), squeeze(mean(AvgWaPower(1:15 ,(5:length(faxis)), 5:length(taxis)-5))),15), colorbar	title(filemat(index,:))	end