% averages wa 3d matsfunction[GMpower] = wa3d2_avg(con, sess);vps = ['NS98004';'NS98006';'NS98007';'NS98008'; 'NS98010'];for vpindex = 1 : size(vps,1)   vp = vps(vpindex,:)   file = [ vp '_' sess '.fl40.E1.wa.mat'];        path = ['ElNino:As_Exps:Alte_Exps:hypnosis_pain:wa']      [path ':' file]      eval(['load ' path ':' file]);      max(max(max(AvgWaPower)))   min(min(min(AvgWaPower)))      if vpindex == 1      sumAvgPower = AvgWaPower;   else      sumAvgPower = sumAvgPower + AvgWaPower;   end         clear AvgWaPower   %figure(vpindex)   %size(corrWaMat);   % caution : freqaxis and taxis for plots only   %freqaxis = [14.64:2.44:61];   %taxis=(-1796:4:1000);   %contourf(taxis,freqaxis, squeeze(corrWaMat(1, [3:22],[51:750] )), 18);          %	title([' vp ' vps(vpindex,:)]);         %	colorbar                       %if vpindex  == 1           %sumMat = squeeze(corrWaMat(1, [3:22],[51:750] ));           %else           %sumMat = sumMat+squeeze(corrWaMat(1, [3:22],[51:750] ));           %end                      %resgammavec(vpindex) = mean(mean(squeeze(corrWaMat(1, [freqbandvec],[timepointvec]))));           %clear corrWaMatendGMpower = sumAvgPower ./size(vps, 1);eval(['save ' path ':GM.' sess '.E1.app'  '.wa.mat GMpower']);