%D_perm_statsfunction[D, D_permut_vec, D_crit] = D_perm_stats(mat1, mat2, Nsens4D);% bestimme D fuer jeden zeitpunkt in matsdisp('')disp('step 1: determine D(t) for all topographies...')for t = 1 : size(mat1,2)	topo1 = mat1(:, t);	topo2 = mat2(:, t);	D(t) = D_stat_topos(topo1, topo2, Nsens4D);end	% baue vektoren fuer permutationsstatistikdisp('step 2: determine permutation distribution...')			matall = [mat1; mat2];			for  index = 1:500; 				indices = randperm(2*Nsens4D * size(mat1, 2));				D_permut_vec(index) = D_stat_topos(matall(indices(1:129)), matall(indices(130:258)), Nsens4D);										if round(index./50)==index./50					fprintf(1,'%g of %g done ...\n',index,500)					end			end					figure(3), hist(D_permut_vec, 100)			[histvec,x] = hist(D_permut_vec, 100);			D_crit = x(95);			