% berechnet Meanpotentials aus ATG.ar konstanz file fuer ein anzugebendes Zeit-% fenster im Batchbetriebfunction [] = mnavr2meanpot(trans, bed, ascii1_name, timewins);%clearif nargin < 3ascii1_name = input ('ascii name for result?      ','s')endif nargin < 2bed = input ('name of condition?         ','s')endif nargin <1trans = 'n'end%timewins = [425 475]  % N1ipsi in emolat= [228 243] =>155-186ms     Npts=951 TSB=-440.89 DI=1.475 SB= 1.000 SC= 1.0 Nchan= 162 => emolatmeg					  % p300 in emolat= [280 300]	=>260-300ms							  % LPC in emolat = [325 375] => 350-450ms					  % P1contra  = [190 200] => 80-100ms					  % P1ipsi = [200 210] => 100-120ms					  % N1contra = [210 220] => 120-140ms					  % Nanterior = [250 300] => 200-300ms					  % spaetst = [400:512] =>500-722ms%timewins = input (' time window to select and average? (in samppoints) (form: [x y; a b; etc])    ');%trans = input ('transformiere nach MCCarthy and wood (y/n)?   ','s')bslvec = [232:300];%bslvec = input('baseline (sample points a:b)      ');%vps = ['IA01';'IA04';'IA05';'IA06';'IA07';'IA08';'IA09';'IA11';'IA16';'IA17';'IA21']; % IAPS%vps = ['02';'04';'05';'06';'07';'08';'09';'10';'11';'12';'14'];  %brot%vps = ['HS97001_';'HS97003_';'HS98005_';'HS98009_';'HS98011_';'HS98013_';'NS98004_';'NS98006_';'NS98007_';'NS98008_';'NS98010_'; 'NS98012_'];   %hypno%vps = ['02';'05';'07';'09';'10';'11';'14';'15';'16';'17'] % emolat%vps = ['01';'02';'04';'05';'06';'07';'08';'09';'10';'11';'12']vps = ['0101';'0103';'0104';'0105';'0106';'0107';'0108';'0109';'0112';'0113']ascii1_name pfad = ['ElNino:users:christof:eltMN']%  pfad = ['UBIES:iaps:atgfiles']    elt0101.at4096MN.avr% pfad = ['UBIES:bruno:erps']for vpindex = 1 : size(vps,1)   % schleife ueber subjects  vp = vps(vpindex,:); file = ['elt'  vp  '.at' bed 'MN.avr'];% file = [ vp '.Merge.at' bed]%file = [ bed vp '.fl40.E1.atg.ar' ]FilePath = [ pfad ':' file]% baseline abziehen[ERPmat] = read_avr_MN(FilePath);   % liest einfor chan = 1 : 129BslCorMat(chan,:) = ERPmat(chan,:)-mean(ERPmat(chan,bslvec));end% berechne  meanpotentials  % transformiere nach MCCarty and wood        if trans == 'y'        for t = 1 : length (BslCorMat);            teilmat = BslCorMat(:,t);            minimum = min(teilmat);            maximum = max(teilmat);                        for elek = 1 : 129                neumat(elek, t) = (BslCorMat(elek,t)-minimum)/(maximum - minimum);            end        end    else neumat = BslCorMat;	end	    % schreibe gemittelte werte fuer jede elektrode in superanova-format 	ERPneuvec1 = mean(neumat(:,[timewins(1,1):timewins(1,2)])');ERPsamat (vpindex, [1:129]) = ERPneuvec1;end if trans == 'y'	 ascii1_name = ([ascii1_name 'MW']); end eval(['save ElNino:temp:'ascii1_name ' ERPsamat -ascii'])%eval (['save ubies:As_exps:Hypnosis_pain:fuerstatsneu:'ascii1_name ' ERPsamat -ascii'])  % hypno%eval (['save ubies:iaps:fuer_stats:'ascii1_name ' ERPsamat -ascii']) %IAPSdisp ('saving file'), disp (ascii1_name)clear 