% adds points function[AvgMatnew]=addpoints(filemat, bslvec);for index = 1:size(filemat,1);         if ~isstruct(filemat);         atgPath = deblank(filemat(index,:));    else        atgPath = filemat(index).name;     end	    [AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,SampRate] = ReadAvgFile(atgPath);          AvgMatnew = [  rand(size(AvgMat,1),50)  AvgMat   zeros(size(AvgMat,1),50)];		[File,Path,FilePath]=SaveAvgFile([atgPath '.t'],AvgMatnew,NTrialAvgVec,[], SampRate);    %eval(['save ' deblank(atgPath) '.pha phasemat -ascii'])end