% avgwinmats% averages SSR-winmats function [] = ascii2mat(filemat);% subjectvec = ['01'; '02'; '04'; '07'; '08'; '09';'10'; '11'; '12';'13';'14';'15';'17'; '18';'19'; '21'; '22'; '24';'25'];%subjectvec = ['201'; '203'];for subject = 1 : size(filemat,1)		s = findstr('.', [filemat(subject,:)]);		matname = [filemat(subject,1:s(1)-1)];		eval(['load ' filemat(subject,:) ' -ascii']); 		eval(['ampmat = ' matname ';']);    save([[filemat(subject,:)] '.mat'],  'ampmat' ,  '-mat')	fprintf('.')		end	