% atg2avr% reads scads atg and writes 2 besa ascii .avr formatfunction [] = atg2longer(filemat, newlength)	for fileindex = 1 : size(filemat, 1)		[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra] = ...	ReadAvgFile(deblank(filemat(fileindex,:)));					a = AvgMat;                 for chan = 1:size(a,1)            b(chan,:)=resample(a(chan,:), newlength,length(a(chan,:)));         end                        SaveAvgFile([FilePath '.l.at'],b,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus); 				end		