% atg2avr% reads scads atg and writes 2 besa ascii .avr formatfunction [] = atg2newname(filemat, extension)	for fileindex = 1 : size(filemat, 1)		[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra] = ...	ReadAvgFile(filemat(fileindex,:));					a = AvgMat;                              SaveAvgFile([FilePath '.'  extension],a,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus); 				end		