function ref = avg_ref_baby129(matrix);% use only top124 channels, the rest is zero because no eye channels...matrix4ref = matrix(1:124,:); % calculate mean for each time stamp = avg, which will be the referencemean_col = mean(matrix4ref', 2);for i=1:length(matrix4ref(1,:)),	ref(:,i) = matrix4ref(:,i)-mean_col(i);end;% now put everything together, add zeros for eye channels, and put avg% refernce at the referencematrix(1:124,:)=ref; matrix(129,:) = mean_col;ref = matrix; 