% avgwinmats% averages SSR-winmats function [avgmat] = avgasciimats(filemat, outname);% subjectvec = ['01'; '02'; '04'; '07'; '08'; '09';'10'; '11'; '12';'13';'14';'15';'17'; '18';'19'; '21'; '22'; '24';'25'];%subjectvec = ['201'; '203'];if nargin <2, outname = 'avg', endfor subject = 1 : size(filemat,1)		s = findstr('.', [filemat(subject,:)]);		matname = [filemat(subject,1:s(1)-1)];		eval(['tmpval = load (''' deblank(filemat(subject,:)) ''');']); 				if subject == 1				summat = tmpval; 	else				summat = summat + tmpval;	end	fprintf('.')		endavgmat = summat ./ size(filemat,1); disp('saving to disk:')disp(outname)save([outname '.dat'],  'avgmat' ,  '-ascii')	