% avgmats% average .mat files specified in filematinfunction [avgmat] = avgmats(filematin, outname)for fileindex = 1 : size(filematin, 1)		load(deblank(filematin(fileindex, :))); 		actmat = hitsbycontrast;	size(actmat)		if fileindex == 1		summat = actmat; 	else		summat = summat + actmat; 	end	endavgmat = summat ./ size(filematin, 1); if ~isempty(outname)    save(outname, 'avgmat', '-mat')end