% avgmats% average .mat files specified in filematinfunction [avgmat] = avgmats_mat(filematin, outname)if nargin < 2, outname = [];endfor fileindex = 1 : size(filematin, 1)		  strucmat = load(deblank(filematin(fileindex, :)));          mat = eval(['strucmat.' char(fieldnames(strucmat))]);		disp(filematin(fileindex, :))	    if fileindex == 1		summat = mat;        else	summat = summat + mat;         endendavgmat = summat ./ size(filematin, 1); if ~isempty(outname)    save(outname, 'avgmat', '-mat')end