%coheretimefunction [allemat] = cohere_as(dirname);% segment fuer long: 2415:3280% taxis = [-200:2.5:7998.5];filemat = getfilesindir(dirname);for fileindex = 1: size(filemat, 1);			data = readavgfile(filemat(fileindex, :));		dummycount = 2;			for elek1 =1:128 			for elek2  = dummycount:129; 										dummyvec = cohere(data(elek1,:),data(elek2,:),21,250);									CoherMat(elek1, elek2)  = dummyvec(2);							end				dummycount = dummycount +1;						end				eval(['save ' filemat(fileindex, :)  '.coh CoherMat  -ascii'])		end	 						