% gruppiert elektroden und/oder Zeitpukte aus 129 sa file in beliebig viele Gruppen%clearfunction [] = finddiffsmax(filemat)if nargin < 1, % 1.) variablen:filemat = [];% 2.) filenamenarray erzeugen[nrfiles, filemat, Pathname, NameList] = readfilenamesAS;endnrfiles = size(filemat, 1); disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' !!!!!!!!!!!!! only one time point must be in sa file !!!!!!!!!!')disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' ')% 3.) files oeffnen und bearbeitenfor fileindex = 1 : nrfileseval(['vollmat = load (''' filemat(fileindex, :) ''');']);disp('file size:')disp(size(vollmat))disp(' ')disp('file index: ')disp(fileindex)% 2.) inputvariablen  if fileindex == 1	nrvp = input ('number of subjects ?      ')	nrgrups = 2	disp('number of electrode groups to subtract from should be 2      ')		suffix = input ('suffix for groupfilename   ?    ','s')	end			% vectoren einlesen und als vector1 vector2 ... ablegen	 	  % beginne eigentlichen Gruppierungsprozess						minvec = min(vollmat')	maxvec = max(vollmat')		grupmat = maxvec - minvec;	grupmat = rad2grad(grupmat);	grupmat = grupmat';	   	colonvec = find(filemat(1,:) == ':');	grupdatei = ([filemat(fileindex, max(colonvec)+1:length(filemat(fileindex,:))) suffix]); 	%[File, Path] = uiputfile('ubies:iapsgam:fuer_stats:*')	disp ('saving file:'), disp (grupdatei)     	eval (['save  elnino:tmpstats:' grupdatei	' grupmat -ascii'])	grupmat = zeros(nrvp,nrgrups);end