% Zeigt einzelne frequenzbaender aus kompletter Feigematrix der reihe nach an % Bedingung file liegt in endres% 1.) bilde differenz-files brot - rrotvps = ['02';'04';'05';'06';'07';'08';'09';'10';'11';'12';'14']pfad = ['UBIES:BRUNO:endres:alle_frequelek32t:']      for num = 1 : size(vps,1)  	clear dif  	clear file1  	clear file2  	  vp = vps(num,:)      file1 = ['brot' vp ];	file2 = ['rrot' vp ];	      			eval (['load ' pfad  file1])      eval (['load ' pfad  file2])	        dif = eval([file1 '-' file2]);         diffile = (['diff' vp])      eval (['save ubies:bruno:endres:diffbrotrrot:'diffile ' dif -ascii'])    	end