% fuer Auswertung der SAM-daten aus emosteadyshownr = input('insert show nr    ')vpnr = input('vp ID     ')%lade drei shows als ascii-vector 1*60lade = input('load showfiles  y/n ', 's')if lade == 'y'load_itendclear actShowif shownr == 1	actShow = show1dat;elseif shownr == 2	actShow = show2dat;elseif shownr == 3	actShow = show3dat;endclear valenceclear arousalfor pic = 1:60	fprintf('picture number in this session: %g ', pic), disp(' ')	fprintf('picture ID:  %g ', actShow(pic)), disp(' ')	IDvec(pic) = actShow(pic);	valence(pic) = input('valence of picture   ');	arousal(pic) = input('arousal of picture    ');endvalencarousal = [IDvec' valence' arousal'];valencearousal = sortrows(valencarousal);eval(['save ubies:tmpstats:' num2str(vpnr) 'valencarousal valencearousal -ascii'])