function [statmat] = get_ERTS_CRT(infilepath, tastencode)% tastencode 1 = ALTGR = BILD% tastencode 2 = ALTGR = PSEUDOBILDfid = fopen(infilepath)% dummylinesdumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  fuer altgr == BILDline = 1index = 1if tastencode == 1			while line > 0				% read data				line = fgets(fid);		if line < 0, break, return, end				trialnum = str2num(line(29:31))				if trialnum ~= index, error('falsch'), end				line				RTvec(index) = str2num(line(38:42));				Key = line(46)				trial_art = [line(61:63) line(67)]				% infer conditions				% 1= pleasant; 2=neutral, 3=unpleasant, 4=pseudowort				if trial_art == 'POSN', condition_vec(index) = 1;		elseif trial_art == 'NEUN', condition_vec(index) = 2;		elseif trial_art == 'NEGN', condition_vec(index) = 3;		elseif trial_art == 'POSV', condition_vec(index) = 4;		elseif trial_art == 'NEUV', condition_vec(index) = 5;		elseif trial_art == 'NEGV', condition_vec(index) = 6;		else condition_vec(index) = 7;		end	% 		% infer correct% 		% 		if condition_vec(index) < 4 & Key == 'A' , correctvec(index) = 1;% 		elseif 	condition_vec(index) > 3 & Key == 'C', correctvec(index) = 1;% 		else correctvec(index) = 0;% 		end% 										index = index + 1;				% for correctflag	endend%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  ende altgr == Bild%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%   ausgabe statmat  statmat = [condition_vec'  RTvec'];indexposN = find(statmat(:,1) == 1)indexntrN = find(statmat(:,1) == 2)indexnegN = find(statmat(:,1) == 3)indexposV = find(statmat(:,1) == 4)indexntrV = find(statmat(:,1) == 5)indexnegV = find(statmat(:,1) == 6)meanposN = mean(statmat(indexposN,2))medianposN = median(statmat(indexposN,2))stdposN = std(statmat(indexposN,2))meanposV = mean(statmat(indexposV,2))medianposV = median(statmat(indexposV,2))stdposV = std(statmat(indexposV,2))meanntrN = mean(statmat(indexntrN,2))medianntrN = median(statmat(indexntrN,2))stdntrN = std(statmat(indexntrN,2))meanntrV = mean(statmat(indexntrV,2))medianntrV = median(statmat(indexntrV,2))stdntrV = std(statmat(indexntrV,2))meannegN = mean(statmat(indexnegN,2))mediannegN = median(statmat(indexnegN,2))stdnegN = std(statmat(indexnegN,2))meannegV = mean(statmat(indexnegV,2))mediannegV = median(statmat(indexnegV,2))stdnegV = std(statmat(indexnegV,2))meanvec = [meanposN meanntrN meannegN meanposV meanntrV meannegV]medianvec = [medianposN medianntrN mediannegN medianposV medianntrV mediannegV]stdvec = [stdposN stdntrN stdnegN stdposV stdntrV stdnegV]eval(['save 'infilepath '.statmat  statmat -ascii'])eval(['save 'infilepath '.meanvec  meanvec -ascii'])eval(['save 'infilepath '.medianvec  medianvec -ascii'])eval(['save 'infilepath '.stdvec  stdvec -ascii'])