function [ERRvec_1bis18, RTvec_1bis18, RTvec1,correctvec,condition_vec] = get_ERTS_checksoa(infilepath, tastencode)% tastencode 1 = ALTGR = target% tastencode 2 = ALTGR = nontragetfid = fopen(infilepath)% dummylinesdumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  fuer altgr == wortline = 1index = 1if tastencode == 1		while line >= 1				% read data				line = fgets(fid);                		if length(line)==1, break, return, end				trialnum = str2num(line(29:31));	            RTvec1(index) = str2num(line(38:43));		        Key1 = line(49);				trial_art = line(24:25);				% infer conditions				% 1=std_P_50; 2=std_N_50, 3=std_U_50, 4=std_P_250; 5=std_N_250,		% 6=std_U_250 7=std_P_750, 8=std_N_750, 9= std_U_750        % 10=v_P_50; 11=v_N_50, 12=v_U_50, 13=v_P_250; 14=v_N_250,		% 15=v_U_250 16=v_P_750, 17=v_N_750, 18=v_U_750		 		condition_vec(index) = str2num(trial_art);% 			% infer correct: correct = 2% 		% hits		if condition_vec(index) < 4 & Key1 == 'A' , correctvec(index) = 2;        elseif 	condition_vec(index) > 3 & Key1 == 'C', correctvec(index) = 2;        elseif 	condition_vec(index) > 6 & Key1 == '0', correctvec(index) = 2;         elseif 	condition_vec(index) > 9 & Key1 == 'A', correctvec(index) = 2;        elseif 	condition_vec(index) > 12 & Key1 == 'C', correctvec(index) = 2;        elseif 	condition_vec(index) > 15 & Key1 == '0', correctvec(index) = 2;                % false alarms und false rejections        elseif condition_vec(index) < 4 & Key1 == 'C' , correctvec(index) = 1;        elseif 	condition_vec(index) > 3 & Key1 == 'A', correctvec(index) = 1;        elseif 	condition_vec(index) > 6 & Key1 ~= '0', correctvec(index) = 1;         elseif 	condition_vec(index) > 9 & Key1 == 'C', correctvec(index) = 1;        elseif 	condition_vec(index) > 12 & Key1 == 'A', correctvec(index) = 1;        elseif 	condition_vec(index) > 15 & Key1 ~= '0', correctvec(index) = 1;                    % misses        elseif condition_vec(index) < 4 & Key1 == '0' , correctvec(index) = 0;        elseif 	condition_vec(index) > 3 & Key1 == '0', correctvec(index) = 0;        elseif 	condition_vec(index) > 9 & Key1 == '0', correctvec(index) = 0;        elseif 	condition_vec(index) > 12 & Key1 == '0', correctvec(index) = 0;                else correctvec(index) = -1;        end		index = index + 1;		endendindex_1 = find(condition_vec == 1 & correctvec == 2); index_2 = find(condition_vec == 2 & correctvec == 2);index_3 = find(condition_vec == 3 & correctvec == 2);index_4 = find(condition_vec == 4 & correctvec == 2)index_5 = find(condition_vec == 5 & correctvec == 2);index_6 = find(condition_vec == 6 & correctvec == 2);index_7 = find(condition_vec == 7 & correctvec == 2);index_8 = find(condition_vec == 8 & correctvec == 2);index_9 = find(condition_vec == 9 & correctvec == 2);index_10 = find(condition_vec == 10 & correctvec == 2);index_11 = find(condition_vec == 11 & correctvec == 2);index_12 = find(condition_vec == 12 & correctvec == 2);index_13 = find(condition_vec == 13 & correctvec == 2);index_14 = find(condition_vec == 14 & correctvec == 2);index_15 = find(condition_vec == 15 & correctvec == 2);index_16 = find(condition_vec == 16 & correctvec == 2);index_17 = find(condition_vec == 17 & correctvec == 2);index_18 = find(condition_vec == 18 & correctvec == 2);mean_1 = mean(RTvec1(index_1));mean_2 = mean(RTvec1(index_2));mean_3 = mean(RTvec1(index_3));mean_4 = mean(RTvec1(index_4));mean_5 = mean(RTvec1(index_5));mean_6 = mean(RTvec1(index_6));mean_7 = mean(RTvec1(index_7));mean_8 = mean(RTvec1(index_8));mean_9 = mean(RTvec1(index_9));mean_10 = mean(RTvec1(index_10));mean_11 = mean(RTvec1(index_11));mean_12 = mean(RTvec1(index_12));mean_13 = mean(RTvec1(index_13));mean_14 = mean(RTvec1(index_14));mean_15 = mean(RTvec1(index_15));mean_16 = mean(RTvec1(index_16));mean_17 = mean(RTvec1(index_17));mean_18 = mean(RTvec1(index_18));RTvec_1bis18 = [mean_1 mean_2 mean_3 mean_4 mean_5 mean_6 mean_7 mean_8 mean_9 mean_10 mean_11 mean_12 mean_13 mean_14 mean_15 mean_16 mean_17 mean_18];% fuer prozent korrekt: corr_1 = length(index_1)./length(find(condition_vec==1)).*100;corr_2 = length(index_2)./length(find(condition_vec==2)).*100;corr_3 = length(index_3)./length(find(condition_vec==3)).*100;corr_4 = length(index_4)./length(find(condition_vec==4)).*100;corr_5 = length(index_5)./length(find(condition_vec==5)).*100;corr_6 = length(index_6)./length(find(condition_vec==6)).*100;corr_7 = length(index_7)./length(find(condition_vec==7)).*100;corr_8 = length(index_8)./length(find(condition_vec==8)).*100;corr_9 = length(index_9)./length(find(condition_vec==9)).*100;corr_10 = length(index_10)./length(find(condition_vec==10)).*100;corr_11 = length(index_11)./length(find(condition_vec==11)).*100;corr_12 = length(index_12)./length(find(condition_vec==12)).*100;corr_13 = length(index_13)./length(find(condition_vec==13)).*100;corr_14 = length(index_14)./length(find(condition_vec==14)).*100;corr_15 = length(index_15)./length(find(condition_vec==15)).*100;corr_16 = length(index_16)./length(find(condition_vec==16)).*100;corr_17 = length(index_17)./length(find(condition_vec==17)).*100;corr_18 = length(index_18)./length(find(condition_vec==18)).*100;ERRvec_1bis18 = [corr_1 corr_2 corr_3 corr_4 corr_5 corr_6 corr_7 corr_8 corr_9 corr_10 corr_11 corr_12 corr_13 corr_14 corr_15 corr_16 corr_17 corr_18];%statmat = [condition_vec' correctvec' RTvec'];eval(['save ' infilepath '.RT RTvec_1bis12 -ascii'])eval(['save ' infilepath '.ER ERRvec_1bis12 -ascii'])bar(RTvec_1bis18), title('RT')figurebar(ERRvec_1bis18), title('errors')