function [statmat] = get_ERTS_res(infilepath, tastencode)% tastencode 1 = ALTGR = wort% tastencode 2 = ALTGR = pseudowortfid = fopen(infilepath)% dummylinesdumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  fuer altgr == wortline = 1index = 1if tastencode == 1			while line > 0				% read data				line = fgets(fid)		if line < 0, break, return, end				trialnum = str2num(line(29:31))				if trialnum ~= index, error('falsch'), end				RTvec(index) = str2num(line(38:42))				Key = line(46)				trial_art = line(61:63)				% infer conditions				% 1= pleasant; 2=neutral, 3=unpleasant, 4=pseudowort				if trial_art == 'ANG', condition_vec(index) = 1;		elseif trial_art == 'NEU', condition_vec(index) = 2;		elseif trial_art == 'UNA', condition_vec(index) = 3;		else condition_vec(index) = 4;		end			% infer correct				if condition_vec(index) < 4 & Key == 'A' , correctvec(index) = 1;		elseif 	condition_vec(index) > 3 & Key == 'C', correctvec(index) = 1;		else correctvec(index) = 0;		end												index = index + 1;				% for correctflag	endendstatmat = [condition_vec' correctvec' RTvec'];