function [picmat,valmat, aromat, allmat] = get_log_olivia(infilepath)fid = fopen(infilepath);dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)dumline = fgetl(fid)datline = 1picmat = []aromat = []valmat = []	while datline > 0			datline = fgets(fid);			if datline < 0, break, return, end		nindex = findstr(datline, 'Picture	n');		uindex = findstr(datline, 'Picture	u');		pindex = findstr(datline, 'Picture	p');		tindex = findstr(datline, 'Picture	t');			if nindex, picname = datline(nindex+7:nindex+12); picmat = [picmat; picname]; end						if uindex, picname = datline(uindex+7:uindex+12); picmat = [picmat; picname]; end						if pindex, picname = datline(pindex+7:pindex+12);picmat = [picmat; picname]; end						if tindex, picname = datline(tindex+7:tindex+12);picmat = [picmat; picname]; end						valindex =	findstr(datline, 'Picture	val-rating: ');			aroindex = 	findstr(datline, 'Picture	aro-rating: ');			if valindex, valrating = str2num(datline(valindex+20)); valmat = [valmat; valrating], end 			if aroindex, arorating = str2num(datline(aroindex+20)); aromat = [aromat; arorating], end 													end	tabmat = '	';	for index = 1: length(valmat)-1; 		tabmat = [tabmat; '	'];	end		allmat =  [picmat, tabmat, num2str(valmat), tabmat, num2str(aromat)];dlmwrite([infilepath '.txt'], allmat, '');