function [condvec] = get_pres_log_2face(filepath_6files)index = 1;condvec = [];for fileindex = 1:6fid = fopen(filepath_6files(fileindex,:))line = 1;% dummylinesdumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);dumline = fgetl(fid);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  			while line > 0								line = fgets(fid)		 fileindex         pause				% segmentiere jede zeile nach tabs				trialnumseg = find(line == '	');				% event-tags		        if line > 0 % read data				eventnum = line(trialnumseg(1)+1:trialnumseg(2)-1);				eventcateg = line(trialnumseg(2)+1:trialnumseg(3)-1);				eventcode = (line(trialnumseg(3)+1:trialnumseg(4)-1))                if strcmp(eventcode, 'iti' )            condvec = condvec        elseif strcmp(eventcode,'11HL') || strcmp(eventcode,'44HL')            condvec(index) = 1; index = index + 1;        elseif strcmp(eventcode,'12HL') || strcmp(eventcode,'45HL')            condvec(index) = 2; index = index + 1;        elseif strcmp(eventcode,'13HL') || strcmp(eventcode,'46HL')            condvec(index) = 3; index = index + 1;        elseif strcmp(eventcode,'21HL') || strcmp(eventcode,'54HL')            condvec(index) = 4; index = index + 1;        elseif strcmp(eventcode,'22HL') || strcmp(eventcode,'55HL')            condvec(index) = 5; index = index + 1;        elseif strcmp(eventcode,'23HL') || strcmp(eventcode,'56HL')            condvec(index) = 6; index = index + 1;        elseif strcmp(eventcode,'31HL') || strcmp(eventcode,'64HL')            condvec(index) = 7; index = index + 1;        elseif strcmp(eventcode,'32HL') || strcmp(eventcode,'65HL')            condvec(index) = 8; index = index + 1;        elseif strcmp(eventcode,'33HL') || strcmp(eventcode,'66HL')            condvec(index) = 9; index = index + 1;                  elseif strcmp(eventcode,'11LH') || strcmp(eventcode,'44LH')            condvec(index) = 11; index = index + 1;        elseif strcmp(eventcode,'12LH') || strcmp(eventcode,'45LH')            condvec(index) = 12; index = index + 1;        elseif strcmp(eventcode,'13LH') || strcmp(eventcode,'46LH')            condvec(index) = 13; index = index + 1;        elseif strcmp(eventcode,'21LH') || strcmp(eventcode,'54LH')            condvec(index) = 14; index = index + 1;        elseif strcmp(eventcode,'22LH') || strcmp(eventcode,'55LH')            condvec(index) = 15; index = index + 1;        elseif strcmp(eventcode,'23LH') || strcmp(eventcode,'56LH')            condvec(index) = 16; index = index + 1;        elseif strcmp(eventcode,'31LH') || strcmp(eventcode,'64LH')            condvec(index) = 17; index = index + 1;        elseif strcmp(eventcode,'32LH') || strcmp(eventcode,'65LH')            condvec(index) = 18; index = index + 1;        elseif strcmp(eventcode,'33LH') || strcmp(eventcode,'66LH')            condvec(index) = 19; index = index + 1;         end		        end				    endend	