% GENERDATNEWfunction[sig1]=get_wavfile(freq,durationsec, outname);t = [0:1/44100:durationsec]; sig1 = sin(2*freq*pi*t)./1.4M2 = round(length(t)/10);squarecos1 = (cos(pi/2:(pi-pi/2)/M2:pi-(pi-pi/2)/M2)).^2;squarecos2 = (cos(0:(pi-pi/2)/M2:pi/2-(pi-pi/2)/M2)).^2;sweep_tap = [sig1(1:M2) .* squarecos1 sig1(M2+1:length(t) - M2) sig1(length(t)-M2+1 : length(t)).*squarecos2];sound(sig1,44100)plot(sig1)if nargin >2wavwrite(sig1, 44100, outname)end