%get freq_baseline berechnet in Bills EXperiment alpha antei in baselineresmat = zeros(5, 129);bed = input ('name of condition?         ','s')ascii1_name = input ('ascii name for result?      ','s')vps = ['HS97003_';'HS98005_';'HS98009_';'HS98011_';'HS98013_';'NS98004_';'NS98006_';'NS98007_';'NS98008_';'NS98010_']; pfad = ['UBIES:hypnosis_pain:hypno']   for vpindex = 1 : size(vps,1)  vp = vps(vpindex,:); file = [ vp bed '.fl40.E1.atg.ar'];FilePath = [ pfad ':' file] ERPmat = readavgfile(FilePath);for channel = 1 : 129;	basevec = ERPmat(channel, [1:64]);	freqvec = psd(basevec,64,250);	alphapower(channel) = freqvec (2,1);endresmat(vpindex,:) = alphapower;endeval(['save ubies:Hypnosis_pain:fuer_stats:' ascii1_name ' resmat -ascii'])disp ('saving file'), disp (ascii1_name)