function [meanblink] = getblink_app(filemat);% change taxis for given studytaxis = -800:4:600;for fileindex = 1 : size(filemat, 1);	[Data,Version,LHeader,ScaleBins,NChan,NPoints,NTrials,SampRate,AvgRefStatus,File,Path,FilePath,EegMegStatus,NChanExtra]=...	ReadAppData(filemat(fileindex, :));			disp('number of trials  :')		disp(NTrials)					for trial = 1 : NTrials; 								data = ReadAppData(filemat(fileindex, :), trial);								veogvec_R = data(126,:) - data(8,:);								plot(taxis, veogvec_R)								[no_use,y] = ginput; 								if length(no_use) == 1; 										blinkvec(trial) = 0;				else										blinkvec(trial) = abs(y(1)-y(2));				end						end							nozeros_ind = find(blinkvec>0);								meanblink = mean(blinkvec(nozeros_ind));								blinkvec				blinkvec(nozeros_ind)								eval(['save ' filemat(fileindex, :) '.Bl meanblink -ascii']);								 				clear blinkvec				clear nozeros_ind												end																				