% getcondveceprime% searches condnumberes in e-prime *.txt filefunction [condvec, RTvec] = getcondveceprime(filepath);condvec = [];RTvec = [];fid = fopen(filepath)a = 1	 while a > 0	a = fgets(fid); 	index1 = findstr(a, 'condNum: '); 		if ~isempty (index1);		condvec = [condvec str2num(a(index1+9))];		end			index2 = findstr(a, 'TextDisplay1.RT: ');		if ~isempty (index2);		RTvec = [RTvec str2num(a(index2+17:length(a)))];		end	 endtrialmat = [condvec' RTvec'];dotindex = find(filepath == '.');outname = [filepath(1:dotindex-1) '.RT']% denksport% RTsind_1 = find(condvec == 1 & RTvec > 202);ind_2 = find(condvec == 2 & RTvec > 202);ind_3 = find(condvec == 3 & RTvec > 202);ind_4 = find(condvec == 4 & RTvec > 202);ind_5 = find(condvec == 5 & RTvec > 202);ind_6 = find(condvec == 6 & RTvec > 202);ind_7 = find(condvec == 7 & RTvec > 202);ind_8 = find(condvec == 8 & RTvec > 202);md_1 = median(RTvec(ind_1))md_2 = median(RTvec(ind_2))md_3 = median(RTvec(ind_3))md_4 = median(RTvec(ind_4))md_5 = median(RTvec(ind_5))md_6 = median(RTvec(ind_6))md_7 = median(RTvec(ind_7))md_8 = median(RTvec(ind_8))err1 = length(find(condvec == 1 & RTvec < 201)) ./ length(find(condvec == 1));err2 = length(find(condvec == 2 & RTvec < 201)) ./ length(find(condvec == 2));err3 = length(find(condvec == 3 & RTvec < 201)) ./ length(find(condvec == 3));err4 = length(find(condvec == 4 & RTvec < 201)) ./ length(find(condvec == 4));err5 = length(find(condvec == 5 & RTvec < 201)) ./ length(find(condvec == 5));err6 = length(find(condvec == 6 & RTvec < 201)) ./ length(find(condvec == 6));err7 = length(find(condvec == 7 & RTvec < 201)) ./ length(find(condvec == 7));err8 = length(find(condvec == 8 & RTvec < 201)) ./ length(find(condvec == 8));RTvec = [md_1 md_2 md_3 md_4 md_5 md_6 md_7 md_8]errvec = [err1 err2 err3 err4 err5 err6 err7 err8]outname1 = [filepath(1:dotindex-1) '.RT']eval(['save ' outname1 ' RTvec -ascii']);outname2 = [filepath(1:dotindex-1) '.err']eval(['save ' outname2 ' errvec -ascii']);fclose('all')