% getmelodyfunction [melody] = getmelody(invec)SR = 44100.0 %Sample rateSW  = 16  %  /* octaves per second */DUR= 120  % sweep durationFAMPEND = 30  % hum?RD  = 10 % /* ms rampduration */AMP  = 103  % ampfacmelody = []; Sample_int  = 1000 / SRIntertone = zeros(1,7000);t =0:Sample_int/1000:DUR/1000;length_t = length(t)	for freq = 1 : length(invec) 		for index = 1 : length(t)			sweep(index) = sin(2*pi*invec(freq)*t(index));			end			% sweep rampen: 		M2 =10 * 44100 / 1000;	squarecos1 = (cos(pi/2:(pi-pi/2)/M2:pi-(pi-pi/2)/M2)).^2;	squarecos2 = (cos(0:(pi-pi/2)/M2:pi/2-(pi-pi/2)/M2)).^2;		sweep_ramp = [sweep(1:M2) .* squarecos1 sweep(M2+1:length(t) - M2) sweep(length(t)-M2+1 : length(t)).*squarecos2];		sweep_end = [sweep_ramp Intertone];		if freq == 1		melody = [Intertone sweep_end]; 	else			melody = [melody sweep_end];	end			end				plot(melody)		sound((melody), 44100)