% gruppiert gleich grosse SA - matrizen nebeneinander in neue Matrixclearnrmats = input('number of matrices to merge  ')newmat = []% string descriptionpath = ['UBIES:iapsgam:endres:'];bedvec = ['pos'; 'neu'; 'neg'];bandvec = ['alphaERP'];for band = 1:1  for bed = 1 : length(bedvec)	file = ['IA'bedvec(bed,:) bandvec(band,:) 'ANT1020']	eval(['load ' path file])	dummymat = eval(file);	newmat = [newmat dummymat];	eval(['cd ' path]);  end  [File, NEWPath] = uiputfile('ubies:iapsgam:fuer_stats:*')  disp ('saving file:'), disp ([NEWPath File])  eval (['save ' NEWPath File	' newmat -ascii'])  newmat = []end		