%elcvecsfunction [endmat] = groupbaby_time(filemat, outname)endmat = [];for fileindex = 1:size(filemat,1)    datamat5t = load(filemat(fileindex,:));    left_ant = [14 11 12 23 19 15 13 8 9 16 17 21 24];    left_post = [18 22 25:29 31:33 36 37];    right_ant = [1 2 53 54 3 55 56:62];    right_post = [40:52];%ant = [2 62];%occ_post = [40 44];%left_temp = [24 25 27];    tempmat = ([mean(datamat5t(left_ant,:)) mean(datamat5t(left_post,:)) mean(datamat5t(right_ant,:)) mean(datamat5t(right_post,:))]);    % tempmat = ([mean(datamat5t(ant,:))]);    endmat = [endmat;tempmat];    fprintf('.')endeval(['save ' outname ' endmat -ascii'])       