% gruppiert elektroden und/oder Zeitpukte aus 129 sa file in beliebig viele Gruppen%clearfunction [] = groupdiffsfree(filemat,NameList )if nargin < 1, % 1.) variablen:filemat = [];% 2.) filenamenarray erzeugen[nrfiles, filemat, Pathname, NameList] = readfilenamesAS;endnrfiles = size(filemat, 1); disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' !!!!!!!!!!!!! only one time point must be in sa file !!!!!!!!!!')disp (' !!!!!!!!!! only two clusters of electrodes are possible !!!!!!!')disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' ')% 3.) files oeffnen und bearbeitenfor fileindex = 1 : nrfileseval(['load ' filemat(fileindex, :) ' -mat']);vollmat = SuVAmatFourier;%vollmat = eval(NameList(fileindex, :));disp('file size:')disp(size(vollmat))disp(' ')disp('file index: ')disp(fileindex)% 2.) inputvariablen  if fileindex == 1	nrvp = input ('number of subjects ?      ')	nrgrups = 2	disp('number of electrode groups to subtract should be 2      ')		suffix = input ('suffix for groupfilename   ?    ','s')	grupmat = zeros (nrvp,nrgrups);			% vectoren einlesen und als vector1 vector2 ... ablegen	 	for c = 1 : nrgrups 		 		elvec = input ('enter vector containing electrode numbers [order: left to right / ant to post] ','s'   ) 	 		eval(['vector'int2str(c) ' = ' elvec]); 	end% 		  end     % beginne eigentlichen Gruppierungsprozess		for elvecindex = 1 : nrgrups			actvec = eval(['vector'int2str(elvecindex)]);		dummymat = vollmat(:,actvec);			a = mean(dummymat')				grupmat(:,elvecindex) = a'		    end 	 	compdiffmat = (grupmat(:, 1) - grupmat(:,2))   % berechnet mean diff zwischen zwei clustern		grupmat2 = angle(compdiffmat)		grupmat2 = rad2grad(grupmat2)		colonvec = find(filemat(1,:) == ':');	grupdatei1 = ([filemat(fileindex, max(colonvec)+1:length(filemat(fileindex,:))-4) suffix]);	grupdatei2 = ([filemat(fileindex, max(colonvec)+1:length(filemat(fileindex,:))-4) suffix '.mat']); 	%[File, Path] = uiputfile('ubies:iapsgam:fuer_stats:*')	disp ('saving files:'), disp (grupdatei1), disp (grupdatei2)     		eval (['save  elnino:tmpstats:' grupdatei1	' grupmat2 -ascii'])	eval (['save  elnino:tmpstats:' grupdatei2	' compdiffmat -mat'])	grupmat = zeros(nrvp,nrgrups);end