% gruppiert elektroden und/oder Zeitpukte aus sa file in 12 Gruppen%clearfunction [] = groupelcs(filemat, namelist);%filemat = [];if nargin < 2	nrfiles = input( 'nr of files ')endif nargin < 1	[nrfiles, filemat] = readfilenamesASendnrfiles= size(filemat, 1);% 2.) filenamenarray erzeugen%[nrfiles, filemat] = readfilenamesAS% 3.) files oeffnen und bearbeitenfor fileindex = 1 : nrfileseval (['load ' filemat(fileindex,:)])vollmat = eval(namelist(fileindex,:));disp('file size:')disp(size(vollmat))disp(' ')disp('file index: ')disp(fileindex)% 2.) inputvariablen  if fileindex == 1	nrvp = input ('number of subjects ?      ')	nrgrups = input ('number of electrode groups ?      ')	suffix = input ('suffix for groupfilename   ?    ','s')	grupmat = zeros (nrvp,nrgrups);	end	% gruppen emogam und emolat % leftant = [39 40 41 42  34:37 28:30 45 46];% leftpost = [50:53 48 57:60 64:66];% % rightant = [121:123 115:118 109:112 104 105];% rightpost = [101 102 96:99 91:93 85:87];% 1.) elektrodengruppen 'Keil' als vektoren% linke hemisphaereleftantinf = [22 127 128 23 18 26  27 33  44 34 39];leftempinf = [40 45 49 46 56 50 57 63];leftposinf = [58 64 69 65 70 71 74 75];leftantsup = [28 24 19 25 20 21 12 13 7];leftempsup = [35 29 30 31 32 36 37 38 41 42 43 47 48];leftpossup = [51 52 53 54 59 60 61 66 67 72];% rechte hemisphaererighantinf = [15 9 14  2 08 122 126 125 1  121 120];rigtempinf = [115 116 114 109 102 101 108 100];righposinf = [83 84 90 89 91 96 95 97];righantsup = [10 3 4 5 107 113 119 124 123];rigtempsup = [81 88 94 99 106 105 104 103 110 111 112 118 117];righpossup = [80 87 93 98 79 86 92 78 85 77];% grupdatei = input ('name for result   ','s')location = ['leftantinf'; 'leftempinf'; 'leftposinf'; 'leftantsup'; 'leftempsup'; 'leftpossup'; 'righantinf'; 'rigtempinf'; 'righposinf'; 'righantsup'; 'rigtempsup'; 'righpossup'];nrgrups = length(location(:,1));% beginne eigentlichen Gruppierungsprozessfor elvecindex = 1 : nrgrups				actvec = eval([location(elvecindex, :)]);		dummymat = vollmat(:,actvec);		a = mean(dummymat')	grupmat(:,elvecindex) = a' end% dummy = mean(resmat');% dummy = dummy'grupdatei = ([deblank(namelist(fileindex,:)) suffix]); %[File, Path] = uiputfile('ubies:iapsgam:fuer_stats:*')disp ('saving file:'), disp (grupdatei)     eval (['save  Elnino:tmpstats:' grupdatei	' grupmat -ascii'])grupmat = zeros(nrvp,nrgrups);end