% gruppiert elektroden und/oder Zeitpukte aus 129 sa file in beliebig viele Gruppen%clearfunction [] = groupelcs24_dir(dirname)disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' !!!!!!!!!!!!! only one time point must be in sa file !!!!!!!!!!')disp (' !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!')disp (' ')struc = dir(dirname)% 3.) files oeffnen und bearbeitenfor fileindex = 1 : size(struc,1);			fname = getfield(struc,{fileindex}, 'name');	eval(['load ' dirname ':'fname])	vollmat = eval(fname);	% 2.) inputvariablen 		if fileindex == 1			suffix = '24El'			end			 % beginne eigentlichen Gruppierungsprozess	 	 load ElNino:MATLAB:Libas:4data:elvec_stat					grupmat = vollmat(:, elvec_stat);			grupdatei = ([fname suffix]); %[File, Path] = uiputfile('ubies:iapsgam:fuer_stats:*')disp ('saving file:'), disp (grupdatei)     eval (['save  Elnino:tmpstats:' grupdatei	' grupmat -ascii'])end