% berechnet Meanpotentials aus ATG.ar konstanz file fuer anzugebende Zeit-% fensterclearbed = input('name of condition?         ','s')timewins = input('time windows to select and average? vector format [1 2 etc oder x:y]   ')nrtimewins = length(timewins)ascii1_name = input('ascii name for result?      ','s')disp('saving file'), disp(ascii1_name)% vps = ['02';'04';'05';'06';'07';'08';'09';'10';'11';'12';'14'];  %brot% vps = ['HS97003_';'HS98005_';'HS97009_';'HS98011_';'HS98013_';'NS98004_';'NS98006_';'NS98007_';'NS98008_';'NS98010_'];   %rrotvps = ['02';'05';'07';'09';'10';'11';'14';'15';'16';'17'] % emolatascii1_name pfad = ['UBIES:AS_Exps:emolat:alleVPFilt:'] ERPsamat = zeros(length(vps(:,1)),129*nrtimewins);for vpindex = 1 : size(vps,1)		ERPsavpvec = [] 		vp = vps(vpindex,:);	file = [ 'vp' vp 'E1.at' bed '.ar.f'];	FilePath = [ pfad ':' file]	% berechne  meanpotentials 	[ERPmat] = ReadAvgFile(FilePath);   % liest ein	for tindex = 1 : length(timewins)			ERPneuvec = ERPmat(:,timewins(tindex)');		ERPsavpvec = [ERPsavpvec ERPneuvec'];	endERPsamat(vpindex,:) = ERPsavpvec;endeval(['save UBIES:AS_Exps:emolat:fuer_stats:'ascii1_name ' ERPsamat -ascii'])