% reads mat with single trials in 3rd dimension and creates at file of% averagefunction[]=mat2avgscads(filemat, SampRate);for index = 1:size(filemat,1);         if ~isstruct(filemat);         atgPath = deblank(filemat(index,:));    else        atgPath = filemat(index).name;     end	   a = load(atgPath);       outmat1 = a.dataout;      outmat2 = mean(outmat1,3); 		[File,Path,FilePath]=SaveAvgFile([atgPath '.atg.ar' ],outmat2,[],[], SampRate);   % [File,Path,FilePath]=SaveAvgFile([atgPath '.hphas'],phasemat,NTrialAvgVec,StdChanTimeMat, SampRate);    %eval(['save ' deblank(atgPath) '.pha phasemat -ascii'])end