function[] = mc_norm(filepath);% function [] = mc_norm();% a = inmat% Funktion normiert Daten, die mit kn2sa erzeugt wurden% nach der Methode von McCarthey & Wood% a = inmat% y = x - min / (max-min) fuer jedes ZF% und fuer jede Bedingung ueber alle Elektroden%% Programmparameter sind im Sourcecode einzutragen%% Januar 1997 - T.Gruber%% prg von hand getestetif nargin == 0, disp('mc_norm(erg1_name, a)'), return, endnargin;a = load(filepath)n_elektroden = 24  %Anzahl der Elektrodenn_bedingungen = 1  %Anzahl der Bedingungenn_zf = 1        %Anzahl an Zeitfensternx1 = [];xmin = [];xmax = [];% Minima und Maxima ermittelnfor e1 = 0 : (n_bedingungen* n_zf)-1  x1 = [];  for e2 = 1 : n_bedingungen * n_zf : n_elektroden * n_zf * n_bedingungen    e2+e1;	  x1 = [x1 a(:,e2+e1)];  end;  xmin = [xmin min(x1')'];  xmax = [xmax max(x1')'];end;size(xmin);% Normierenfor n_vp = 1 : size(xmax,1)  for e1 = 0 : (n_bedingungen* n_zf)-1    for e2 = 1 : n_bedingungen * n_zf : n_elektroden * n_zf * n_bedingungen      a(n_vp,e2+e1) = ((a(n_vp,e2+e1)-xmin(n_vp,(e1+1))) /(xmax(n_vp,(e1+1)) - xmin(n_vp,(e1+1))));    end;  end;end;x = a;size(x);% Ergebnis Struktur als Ascii-File Speichernif ~isempty(erg1_name)  s = ['save ' [filepath '.mc' ] ' x -ascii;' ];  eval (s);end