% mergecheckatgs%merged 2 checkatgs (was sonst)function [] = merge_check(vpnum_string, directory)condnames = ['1'; '2'; '3'; '4'; '5'; '6'; '7'; '8'];targnames = ['gd'; 'gh'; 'kd'; 'kh'];runnames = ['1';'2'];% ERPsfor targ = 1 : 4	for cond = 1 : 8			FileMatIn = ([directory 'vp' vpnum_string targnames(targ,:) '1.E1.at' condnames(cond, :) '.ar' ;...					  directory 'vp' vpnum_string targnames(targ,:) '2.E1.at' condnames(cond, :) '.ar'])		FileMatOut = [directory targnames(targ,:) '_vp' vpnum_string 'at' condnames(cond, :) '.ar']		MergeAvgFiles(FileMatIn,FileMatOut,2)	endendfor cond = 1 : 8 	FileMatIn = [directory targnames(1,:) '_vp' vpnum_string 'at' condnames(cond, :) '.ar';...				 directory targnames(2,:) '_vp' vpnum_string 'at' condnames(cond, :) '.ar';...				 directory targnames(3,:) '_vp' vpnum_string 'at' condnames(cond, :) '.ar';...				 directory targnames(4,:) '_vp' vpnum_string 'at' condnames(cond, :) '.ar']		FileMatOut = [directory 'Avg_' condnames(cond, :) '_vp' vpnum_string '.atg.ar']	MergeAvgFiles(FileMatIn,FileMatOut,2)end% 	% appfilesfor cond = 1 : 8	FileMatIn = [directory 'vp' vpnum_string targnames(1,:) '1.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(1,:) '2.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(2,:) '1.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(2,:) '2.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(3,:) '1.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(3,:) '2.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(4,:) '1.E1.app' condnames(cond, :);...				 directory 'vp' vpnum_string targnames(4,:) '2.E1.app' condnames(cond, :)]				 		FileMatOut = [directory 'App_' condnames(cond, :) '_vp' vpnum_string '.app']	MergeAppFiles(FileMatIn,FileMatOut,[])	end				