% picnum2sixconsfunction [condvec, meanVtvec] = picnum2sixcons_vt(infilepath)	VTpicmat = load(infilepath);picvec = VTpicmat(:, 2);vtvec = VTpicmat(:, 1);for picindex = 1 : length(picvec);		if picvec(picindex) < 2050 | picvec(picindex) == 3530 | (picvec(picindex) > 6250 & picvec(picindex) < 6541)	condvec(picindex) = 5;	elseif (picvec(picindex) > 2049 & picvec(picindex) < 2190) | (picvec(picindex) > 2310 & picvec(picindex) < 2361)	condvec(picindex) = 1;	elseif (picvec(picindex) > 2189 & picvec(picindex) < 2231) | (picvec(picindex) > 2361 & picvec(picindex) < 2851) | picvec(picindex) == 9070	condvec(picindex) = 3;	elseif (picvec(picindex) > 2999 & picvec(picindex) < 3131) |  picvec(picindex) == 9405	condvec(picindex) = 6;	elseif (picvec(picindex) > 4649 & picvec(picindex) < 4691) 	condvec(picindex) = 2;	elseif (picvec(picindex) > 7001 & picvec(picindex) < 7236) 	condvec(picindex) = 4;		else	condvec(picindex) = 0; disp ('error: condvec == 0'), pause	endendcondvec = condvec';for con = 1 : 6meanVtvec(con) = mean(vtvec(find(condvec==con)));endeval(['save 'infilepath 'mean meanVtvec -ascii'])		