% plotsingtrial%%function [outmat] = plotsingtrial(appfilepath, chanindex, bslvec);[Data,Version,LHeader,ScaleBins,NChan,NPoints,NTrials,SampRate,AvgRefStatus,File,Path,FilePath,EegMegStatus,NChanExtra]=...	ReadAppData(appfilepath,1,1,[],[],[]);		NTrials		outmat = [];	for trial = 1: NTrials;		[Data,Version,LHeader,ScaleBins,NChan,NPoints,NTrials,SampRate,AvgRefStatus,File,Path,FilePath,EegMegStatus,NChanExtra]=...	ReadAppData(appfilepath,trial,chanindex);	outmat = [outmat; Data];endoutmat = bslcorr(outmat, bslvec);contourf(outmat), colorbar