% regression% compute linear regression and subtracts regression from input % vectorfunction[DetrVec, regkoefs, reggerade] = regression(InVec)% make sure InVec is column vector	dummy = size(InVec);	if dummy(1) == 1;		InVec = InVec';	end				X = [ones(1,length(InVec));1:length(InVec)]';      		  regkoefs = pinv(X' * X) * (X' * InVec);		  reggerade = [1:length(InVec)].*regkoefs(2,1) + regkoefs(1,1);		  DetrVec = InVec - reggerade';	     	 