function [NFiles]=Tmp(InFileMat)if nargin<1; InFileMat=[]; end;[NFiles,InFileMat]=ReadFileNames(InFileMat,'*.app*','Choose App files:');if NFiles<1; return; endfor FileIndex=1:NFiles	[File,Path,FilePath]=GetFileNameOfMat(InFileMat,FileIndex);	InFid=fopen(FilePath,'r+');	Version=fread(InFid,1,'int16');	LHeader=fread(InFid,1,'int16');	ScaleBins=fread(InFid,1,'int16');	NChan=fread(InFid,1,'int16');	NPoints=fread(InFid,1,'int16')	if NPoints~=2048; error(['Da ist was faul: NPoints = ',int2str(NPoints)]); fclose(InFid); end	fseek(InFid,-2,0);	fwrite(InFid,NPoints./4,'int16');	fclose(InFid);end