% rms.m% computes rms for avf or atg - format matrix, i.e. columns are % time pointsfunction [outvec] = rms_path; [nfiles, filemat] = ReadFileNames; for index = 1 : size(filemat,1);     	a = ReadAvgFile(filemat(index,:));		inmat = bslcorr(a,[25:75]);		for timepoint = 1:size(inmat,2)				GPvec(timepoint) = (sum(inmat(1:129, timepoint) .^ 2)) ./ 129;			end		mittel1 = mean(GPvec(90:115))    mittel2 = mean(GPvec(150:175))    mittel3 = mean(GPvec(175:190))    mittel4 = mean(GPvec(190:212))    mittel5 = mean(GPvec(212:262))    outvec = [mittel1 mittel2 mittel3 mittel4 mittel5];eval(['save ' filemat(index,:) '.gp  outvec -ascii'])    end