% show2plotsfunction [] = show2plots(inmatpath)load (inmatpath); faxis = 10*0.9766:2*0.9766:80*0.9766;taxis = [-175*2:2:336*2];figuresubplot(2,2,1), contourf(taxis(80:380), faxis(7:32), squeeze(mean(bslmat_3min4 ([1:35], (7:32), (80:380)))), 10);  colorbar; title('anterior'), xlabel(inmatpath); subplot(2,2,2), contourf(taxis(80:380), faxis(7:32), squeeze(mean(bslmat_3min4 ([60:100], (7:32), (80:380)))), 10),colorbar; title('broad posterior')subplot(2,2,3), contourf(taxis(80:380), faxis(7:32), squeeze(mean(bslmat_3min4 ([50:70], (7:32), (80:380)))), 10);,colorbar; title('left post')subplot(2,2,4), contourf(taxis(80:380), faxis(7:32), squeeze(mean(bslmat_3min4 ([83:103], (7:32), (80:380)))), 10);colorbar; title('right post')inmatpath(length(inmatpath)-15:length(inmatpath)-8)%saveJPG(['elnino:users:kelly:'inmatpath(length(inmatpath)-20:length(inmatpath)-15)])