% atg2avr% reads scads atg and writes 2 besa ascii .avr formatfunction [] = spec2SNR(filemat, bin, extension)	for fileindex = 1 : size(filemat, 1)        	[AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra] = ...	ReadAvgFile(deblank(filemat(fileindex,:)));		a = AvgMat;                 b = a(:, bin);                 c = b./mean(a(:, [bin-5:bin-2 bin+2:bin+5]), 2);             % c = b./mean(a, 2);                         SaveAvgFile([FilePath '.' extension '.SNR.at'],c,NTrialAvgVec,StdChanTimeMat, ...    SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus); 				end		