%==================================================================%%	stead2phaselock%% sliding window procedure%%!!!!!!!!!!! %	%	Function definition function [fftamp, fftcomplexphasevec, phasestabmat, phaselock, tempdiff] = stead2phaselockgenericall(filemat, plotflag, bslvec, ssvepvec,foi,samprate);    if nargin < 2, plotflag = [], end   % this to be done outside the loop to save time, needed for winshift proc sampcycle=1000/samprate; %added code tempvec = round((1000/foi)/sampcycle); % this makes sure that average win duration is exactly @@, which is the duration in sp of one cyle at @@ Hz = @@ ms, sampled at xy Hz longvec = repmat(tempvec,1,500); % repeat this many times, at least for duration of entire epoch, subsegments are created later  winshiftvec_long = cumsum(longvec)+ ssvepvec(1); % use cumsum function to create growing vector of indices for start of the winshift tempindexvec = find(winshiftvec_long > ssvepvec(length(ssvepvec)));   endindex = tempindexvec(1);  % this is the first index for which the winshiftvector exceeds the data segment  winshiftvec = winshiftvec_long(1:endindex-5); size(winshiftvec)  shiftcycle=round(tempvec*4);  samp=1000/samprate;        freqres = 1000/(shiftcycle*samp); %added code to find the appropriate bin for the frequency of interest for each segment        freqbins = 0:freqres:(samprate/2);        Tolerance=0.05;        targetbin=find(abs(freqbins-foi)<Tolerance);for fileindex = 1 : size(filemat,1);        fftamp = [];     fftcomplexphasevec = [];     	FilePath = filemat(fileindex,:);   			[rawmat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...		SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra]= ReadAvgFile(FilePath);		%============================================================	% 2. Baseline correction	%===========================================================		disp ('subtracting baseline')	    datamat = bslcorr(rawmat, bslvec);    size(datamat)	%===========================================================	% 3. moving window procedure with 4 cycles @ 12 Hz !!!	% 	%===========================================================	disp('moving window procedure')		winmatsum = zeros(size(datamat,1),shiftcycle); %4 cycles		if plotflag, h = figure, end       for winshiftstep = 1:length(winshiftvec)		        winmatsum = (winmatsum + regressionMAT(datamat(:,[winshiftvec(winshiftstep):winshiftvec(winshiftstep)+(shiftcycle-1)]))); % time domain averaging for win file                %calculate complex fft on segment        trialmat = regressionMAT(datamat(:,[winshiftvec(winshiftstep):winshiftvec(winshiftstep)+(shiftcycle-1)]));          % amplitude for each segment       NFFT = shiftcycle-1;        NumUniquePts = ceil((NFFT+1)/2); 	   fftmat = fft(trialmat(:,1:(shiftcycle-1))'); % transpose: channels as columns (fft columnwise)       Mag = abs(fftmat);                                                   % Amplitude calc       Mag = Mag*2;   	        Mag(1) = Mag(1)/2;                                                    % DC was unique        if ~rem(NFFT,2),                                                    % Nyquist Frequency unique            Mag(length(Mag))=Mag(length(Mag))/2;        end        Mag=Mag/NFFT; % scale fft so it is independent of NFFT                            fftamp = [fftamp; Mag((targetbin),:)];                  % phase for each segment     fftcomplexphasevec = [fftcomplexphasevec; fftmat((targetbin),:)];                           if plotflag           subplot(3,1,2), plot(1:4:shiftcycle*4, regressionMAT(datamat(:,[winshiftvec(winshiftstep):winshiftvec(winshiftstep)+(shiftcycle-1)]))'), title(['sliding window starting at ' num2str((winshiftvec(winshiftstep))*4)  ' ms ']), xlabel('time in milliseconds')           subplot(3,1,1), plot(1:4:shiftcycle*4, winmatsum'), title(['sum of sliding windows; number of shifts:' num2str(winshiftstep) ]), ylabel('microvolts')          %    subplot(3,1,3), hold on, circle([0,0],1,200,'-');          %    plot([0;(imag(fouriercomp(120)./tenHZampfft(120)))], [0;(real(fouriercomp(120)./tenHZampfft(120)))]);title('phase angle of window')            pause(.4)       end          %    movmat(index) = getframe(h)     end        winmat = winmatsum./length(winshiftvec);	%===========================================================	% 5. determine amplitude and Phase using fft of the winmat (i.e. the	% average	%===========================================================	disp ('determining 12 Hz Phase per channel')		% FFT of the average winmat	% 		NFFT = shiftcycle-1; %one cycle in sp of the desired frequency times 4 oscillations (-1)	NumUniquePts = ceil((NFFT+1)/2); 	fftMat = fft (winmat', (shiftcycle-1));  % transpose: channels as columns (fft columnwise)	Mag = abs(fftMat);                                                   % Amplitude berechnen	Mag = Mag*2;   		Mag(1) = Mag(1)/2;                                                    % DC trat aber nicht doppelt auf	if ~rem(NFFT,2),                                                    % Nyquist Frequenz (falls vorhanden) auch nicht doppelt        Mag(length(Mag))=Mag(length(Mag))/2;	end		Mag=Mag/NFFT;                                                         % FFT so skalieren, da? sie keine Funktion von NFFT ist    	        end % trials	% 	% 	%===========================================================% 	% 6. determine phaselocking and write out results% 	%===========================================================% 	% amplitude%fftamp = fftamp'; %SaveAvgFile([FilePath '.tvamp' ],fftamp,[],[],SampRate,[],[],EegMegStatus)% phase stabilityfftcomplexphasevec = fftcomplexphasevec'; fftcomplexphasevec_norm = fftcomplexphasevec./abs(fftcomplexphasevec); phasestabmat = abs(mean(fftcomplexphasevec_norm, 2)); % intersite phaselocking with reference sensorsize(fftcomplexphasevec_norm)for sens = 1:size(rawmat,1); tempdiff = fftcomplexphasevec_norm - repmat(fftcomplexphasevec_norm(sens,:), size(rawmat,1),1);tempdiff = tempdiff ./(abs(tempdiff));phaselock = abs(mean(tempdiff, 2));phaselock = [phaselock abs(mean(tempdiff, 2))];phaselock(sens,:) = mean(phaselock(sens,:),1);endampmat = fftamp'; % 	Phasevec = Phasevec';% 	tenHZampfft = tenHZampfft';% 		SaveAvgFile([FilePath '.phalock' ],phaselock,[],[],SampRate,[],[],EegMegStatus)%          	SaveAvgFile([FilePath '.win' ],winmat,NTrialAvgVec,[],SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus)%         	SaveAvgFile([FilePath '.phastab' ],phasestabmat,NTrialAvgVec,[],SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus)        SaveAvgFile([FilePath '.winamp' ],ampmat,NTrialAvgVec,[],SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus)end