%temporaeres filePath = [][File, Path] = uigetfile('ubies:*', 'file to open')while Path ~= 0		eval(['load 'Path File])	mat = eval(File);	neumat = zeros(10,129);	for shift = 1 : 5		neumat = neumat + mat(:,129*shift-128 : 129*shift);	end	endmat = neumat./7;	eval(['save 'Path File '  endmat  -ascii'])	[File, Path] = uigetfile('ubies:*', 'file to open')	end