%tempmerge_avrfunction [] = tempmergeavr(condition)controls = ['ak'; 'cc'; 'cd'; 'dm';'jo';'js';'Mk';'sd';'sf';'sm'; 'uv'; 'N2']inpath = 'ElNino:AS_Exps:SSR_schiz:avrfiles_alt:'outpath = 'ElNino:AS_Exps:SSR_schiz:avrfiles_neu:'		for subjectnum = 1 : 12				filepath1 = [inpath 'K_' controls(subjectnum, :) '1_' condition '.avr']						filepath2 = [inpath 'K_' controls(subjectnum, :) '2_' condition '.avr']			fileout = [outpath 'K_' controls(subjectnum, :) '_' condition '.avr']			% Npts= 3280 TSB= -200.00 DI= 2.500 SB= 1.000 SC= 200.0			[a, latencies1] = read_avr(filepath1);			[b, latencies2] = read_avr(filepath2);						size(a)			size(b)						pause			%write_avr(((a+b)./2), [fileout], -200, 2.5);					end		