%% Procedure um *.TXT Protokolle% aus dem Snodgrass Experiment% in *.CON files fuer M. Junghoefers% AvgAppTimeBatch umzuwandeln%% Parameter sind vom Benutzer im% Source Code einzutragen%% ! Da es sich um PC-files handelt muss% ! die Schriftart vorher auf 'Times'% ! umgestellt werden.%% August 1998 - T. Gruber%function[convec]=text2connew(filepath)% Parameter %%%% vom User einzutragen %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%Name des Protokollfiles%%%%%%%%%%%%%%%%%%%%%%%%%%%%disp('');disp('!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!');disp('!!! Stelle sicher, dass im *.TXT file, die Schrift auf  !!!');disp('!!! TIMES geaendert wurde                               !!!');disp('!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!');disp('');convec = [];% Programm[fid, msg] = fopen(filepath, 'r' );if fid == -1  error(msg);end;% Block 1 einlesen% dabei werden Bilder, die schon vorher erkannt wurden,% als Bedingung '5' behandelt		while 1            line = fgetl(fid);            if ~isstr(line), break, end			if ~isempty(findstr(line, 'condNum:'));				max(findstr(line, 'condNum:'));				convec = [convec;line(max(findstr(line, 'condNum:'))+9)];			end			        end        fclose(fid);		convec = str2num(convec);				